mapper.select:
select o.id, o.user_name userName, o.user_phone userPhone, o.user_sex sex, a.agency_name as 'agencyName', p.province_name as 'provinceName', c.city_name as 'cityName', car.name as 'carName', ch.name as 'channel', ch.url as 'channelUrl', o.create_time createTime from t_order o join t_agency a on o.agency_id = a.id join t_city c on o.city_id = c.id join t_province p on o.province_id = p.id join t_car car on car.id = o.car_type_id join t_channel ch on o.channel_id = ch.id
o.user_name like concate('%',#{userName},'%')
and o.user_phone = #{phone}
and o.agency_id = #{anencyId}
and o.city_id = #{cityId}
and o.province_id = #{provinceId}
and o.channel_id = #{channlId}
and o.car_type_id = #{carTypeId}
limit #{offset},#{limit};

mapper.update:
update t_order


        user_name = #{userName}


        user_phone = #{userPhone}


        user_sex = #{sex}


        agency_id = #{agencyId}


        city_id = #{cityId}


        province_id = #{provinceId}


        car_type_id = #{carTypeId}


        status = #{status}



where id=#{id}
